contracts/FactoryAuthorized.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6 <=0.8.20;

import "@openzeppelin/contracts-new/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-new/access/IAccessControl.sol";
import "@openzeppelin/contracts-new/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts-new/security/Pausable.sol";

import "./libraries/FortunnaLib.sol";
import "./libraries/FortunnaErrorsLib.sol";

/// @title A contract that helps to manage the state of the Fortunna contracts.
/// @author Fortunna Team
/// @notice The contract contains protection from the direct calls and roles management.
abstract contract FactoryAuthorized is
    Initializable,
    Pausable,
    ReentrancyGuard
{
    /// @dev An address of the actual contract instance. The original address as part of the context.
    address internal immutable __self = address(this);

    /// @dev An address of the FortunnaFactory contract.
    address internal _factory;

    /// @dev A protection from the direct call modifier.
    modifier delegatedOnly() {
        if (_isInitializing() || __self == address(this)) {
            revert FortunnaErrorsLib.NotInitialized();
        }
        _;
    }

    /// @dev An internal function that checks if a certain `role` is granted to the sender.
    /// @param role A role hash.
    function _onlyRoleInFactory(bytes32 role) internal view {
        address sender = _msgSender();
        if (!IAccessControl(_factory).hasRole(role, sender)) {
            revert FortunnaErrorsLib.NotAuthorized(role, sender);
        }
    }

    /// @dev A modifier that allows only the admin sender to proceed.
    modifier onlyAdmin() {
        // 0x00 == DEFAULT_ADMIN_ROLE
        _onlyRoleInFactory(0x00);
        _;
    }

    /// @dev A modifier that allows only a certain `role` bearer to proceed.
    /// @param role A role hash.
    modifier only(bytes32 role) {
        _onlyRoleInFactory(role);
        _;
    }

    /// @dev An internal initializer which stores an address pointer to the FortunnaFactory instance.
    /// @param __factory The factory address.
    function _initialize(address __factory) internal {
        _factory = __factory;
    }

    /// @notice Triggers paused state.
    /// @dev Could be called only by the admin.
    function pause() external onlyAdmin {
        _pause();
    }

    /// @notice Returns to normal state.
    /// @dev Could be called only by the admin.
    function unpause() external onlyAdmin {
        _unpause();
    }
}

contracts/FortunnaFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6 <=0.8.20;

import "@openzeppelin/contracts-new/access/AccessControl.sol";
import "@openzeppelin/contracts-new/proxy/Clones.sol";
import "@openzeppelin/contracts-new/utils/structs/BitMaps.sol";
import "@openzeppelin/contracts-new/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-new/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-new/utils/structs/EnumerableSet.sol";

import "./interfaces/IFortunnaFactory.sol";
import "./interfaces/IFortunnaPool.sol";
import "./interfaces/IFortunnaToken.sol";

/// @title Canonical Fortunna Yield Farming pools factory
/// @author Fortunna Team
/// @notice Deploys Fortunna Yield Farming pools and manages ownership and control over pool protocol fees.
contract FortunnaFactory is AccessControl, IFortunnaFactory {
    using SafeERC20 for IERC20;
    using FortunnaBitMaskLib for bytes32;
    using Clones for address;
    using EnumerableSet for EnumerableSet.AddressSet;

    /// @inheritdoc IFortunnaFactory
    FortunnaLib.PaymentInfo public override paymentInfo;

    /// @dev A set of unique deployed pools.
    EnumerableSet.AddressSet internal pools;

    /// @dev A set of unique deployed prototypes.
    EnumerableSet.AddressSet internal prototypes;

    /// @notice An index in `prototypes` of FortunnaToken. Depends on the addition process in the initialization function.
    uint256 public constant override FORTUNNA_TOKEN_PROTO_INDEX = 2;

    /// @notice A constructor.
    /// @param paymentTokens An array of tokens addresses to be allowed as payment for pool deploy tokens.
    constructor(
        address _fortunnaTokenPrototype,
        address _fortunnaPoolPrototype,
        address _fortunnaPoolUniswapV3Prototype,
        address[] memory paymentTokens
    ) {
        address sender = _msgSender();
        _grantRole(DEFAULT_ADMIN_ROLE, sender);
        _grantRole(FortunnaLib.POOL_REWARDS_PROVIDER, sender);
        _grantRole(FortunnaLib.ALLOWED_PAYMENT_TOKEN_ROLE, address(0));
        prototypes.add(_fortunnaPoolPrototype);
        prototypes.add(_fortunnaPoolUniswapV3Prototype);
        prototypes.add(_fortunnaTokenPrototype);
        for (uint256 i = 0; i < paymentTokens.length; i++) {
            _grantRole(
                FortunnaLib.ALLOWED_PAYMENT_TOKEN_ROLE,
                paymentTokens[i]
            );
        }
    }

    /// @inheritdoc IFortunnaFactory
    function getPoolAt(uint256 index) external view override returns (address) {
        return pools.at(index);
    }

    /// @inheritdoc IFortunnaFactory
    function getPoolsLength() external view override returns (uint256) {
        return pools.length();
    }

    /// @inheritdoc IFortunnaFactory
    function setPaymentInfo(
        FortunnaLib.PaymentInfo calldata _paymentInfo
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        paymentInfo = _paymentInfo;
    }

    /// @inheritdoc IFortunnaFactory
    function addPrototype(
        address prototype
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        prototypes.add(prototype);
    }

    /// @inheritdoc AccessControl
    function renounceRole(bytes32 role, address account) public override {
        if (hasRole(FortunnaLib.BANNED_ROLE, account))
            revert FortunnaErrorsLib.Banned(account);
        super.renounceRole(role, account);
    }

    /// @inheritdoc IFortunnaFactory
    function generateMask(
        bool[] calldata flags
    ) external pure override returns (bytes32) {
        return FortunnaBitMaskLib.getMaskFromBooleans(flags);
    }

    /// @dev An internal function that validates the addresses if they're allowed
    /// to be used as staking, reward, or external tokens and if the arrays lengths are less then 256.
    /// Also there is a check if `_utilizingTokens` and `_externalRewardTokens` are unique arrays.
    /// @param sender Alias for `_msgSender()`.
    /// @param stakingTokensMask A bit mask to define if the `utilizingTokens` token is for staking.
    /// @param rewardTokensMask A bit mask to define if the `utilizingTokens` token is for user rewards.
    /// @param utilizingTokens An array of tokens either for stake or for rewards.
    function _validateRoles(
        address sender,
        uint256 initialRewardAmountsLength,
        uint256 initialDepositAmountsLength,
        bytes32 stakingTokensMask,
        bytes32 rewardTokensMask,
        address[] calldata utilizingTokens
    ) internal view {
        if (hasRole(FortunnaLib.BANNED_ROLE, sender))
            revert FortunnaErrorsLib.Banned(sender);
        if (utilizingTokens.length > 256) {
            revert FortunnaErrorsLib.InvalidLength(
                utilizingTokens.length,
                "utilizingTokens>256"
            );
        }
        for (uint8 i = 0; i < utilizingTokens.length; i++) {
            address token = utilizingTokens[i];
            if (stakingTokensMask.isBitUp(i)) {
                if (!hasRole(FortunnaLib.ALLOWED_STAKING_TOKEN_ROLE, token)) {
                    revert FortunnaErrorsLib.NotAuthorized(
                        FortunnaLib.ALLOWED_STAKING_TOKEN_ROLE,
                        token
                    );
                }
            }
            if (rewardTokensMask.isBitUp(i)) {
                if (!hasRole(FortunnaLib.ALLOWED_REWARD_TOKEN_ROLE, token)) {
                    revert FortunnaErrorsLib.NotAuthorized(
                        FortunnaLib.ALLOWED_REWARD_TOKEN_ROLE,
                        token
                    );
                }
            }
        }
        if (initialRewardAmountsLength != utilizingTokens.length) {
            revert FortunnaErrorsLib.AreNotEqual(
                initialRewardAmountsLength,
                utilizingTokens.length,
                "tokensLen!=initRewardLen"
            );
        }

        if (initialDepositAmountsLength != utilizingTokens.length) {
            revert FortunnaErrorsLib.AreNotEqual(
                initialDepositAmountsLength,
                utilizingTokens.length,
                "tokensLen!=initDepositLen"
            );
        }
    }

    /// @dev An internal function that checks scalar parameters of the pools.
    /// Firstly, if the chainIds are as expected equal. Secondly, if the start and end timestamps making
    /// a valid time interval. Thirdly, if min and max stake amounts are making also a valid interval. Then,
    /// if early withdrawal fee and deposit/withdraw fee are represented as base points validly.
    /// @param _poolParameters A struct containing the scalar parameters of the pool.
    function _validateScalarParameters(
        FortunnaLib.PoolParameters calldata _poolParameters
    ) internal view {
        if (prototypes.at(_poolParameters.protoPoolIdx) == address(0)) {
            revert FortunnaErrorsLib.UnknownPrototypeIndex(
                _poolParameters.protoPoolIdx
            );
        }
        if (_poolParameters.startTimestamp > _poolParameters.endTimestamp) {
            revert FortunnaErrorsLib.IncorrectInterval(
                _poolParameters.startTimestamp,
                _poolParameters.endTimestamp,
                "time"
            );
        }
        if (_poolParameters.minStakeAmount > _poolParameters.maxStakeAmount) {
            revert FortunnaErrorsLib.IncorrectInterval(
                _poolParameters.minStakeAmount,
                _poolParameters.maxStakeAmount,
                "stakeAmount"
            );
        }
        if (
            _poolParameters.earlyWithdrawalFeeBasePoints >
            FortunnaLib.BASE_POINTS_MAX
        ) {
            revert FortunnaErrorsLib.IncorrectBasePoints(
                _poolParameters.earlyWithdrawalFeeBasePoints,
                "earlyWithdrawal"
            );
        }
        if (
            _poolParameters.depositWithdrawFeeBasePoints >
            FortunnaLib.BASE_POINTS_MAX
        ) {
            revert FortunnaErrorsLib.IncorrectBasePoints(
                _poolParameters.depositWithdrawFeeBasePoints,
                "depositWithdraw"
            );
        }
        if (
            _poolParameters.totalRewardBasePointsPerDistribution >
            FortunnaLib.BASE_POINTS_MAX
        ) {
            revert FortunnaErrorsLib.IncorrectBasePoints(
                _poolParameters.totalRewardBasePointsPerDistribution,
                "rewardBasePoints"
            );
        }
    }

    /// @inheritdoc IFortunnaFactory
    function predictPoolAddress(
        uint256 poolProtoIdx
    ) public view override returns (address result, bytes32 salt) {
        address poolPrototypeAddress = prototypes.at(poolProtoIdx);
        salt = keccak256(abi.encodePacked(pools.length()));
        result = poolPrototypeAddress.predictDeterministicAddress(salt);
    }

    /// @inheritdoc IFortunnaFactory
    function predictFortunnaTokenAddress(
        uint256 poolProtoIdx,
        uint256 poolIdx,
        bool isStakingOrReward
    ) external view override returns (address result, bytes32 salt) {
        salt = keccak256(abi.encodePacked(poolIdx, isStakingOrReward));
        address fortunnaTokenPrototype = prototypes.at(FORTUNNA_TOKEN_PROTO_INDEX);
        address tokenDeployer = prototypes.at(poolProtoIdx)
            .predictDeterministicAddress(keccak256(abi.encodePacked(poolIdx)));
        result = fortunnaTokenPrototype.predictDeterministicAddress(salt, tokenDeployer);
    }

    /// @inheritdoc IFortunnaFactory
    function createPool(
        FortunnaLib.PoolParameters calldata poolParameters,
        FortunnaLib.PoolParametersArrays calldata poolParametersArrays
    ) external payable override returns (address pool) {
        address sender = _msgSender();
        _validateRoles(
            sender,
            poolParametersArrays.initialRewardAmounts.length,
            poolParametersArrays.initialDepositAmounts.length,
            poolParameters.stakingTokensMask,
            poolParameters.rewardTokensMask,
            poolParametersArrays.utilizingTokens
        );
        _validateScalarParameters(poolParameters);

        if (paymentInfo.paymentToken == address(0)) {
            if (msg.value < paymentInfo.cost) {
                revert FortunnaErrorsLib.NotEnoughtPayment(msg.value);
            }
        } else {
            IERC20(paymentInfo.paymentToken).safeTransferFrom(
                sender,
                address(this),
                paymentInfo.cost
            );
        }

        bytes32 deploySalt;
        (pool, deploySalt) = predictPoolAddress(
            poolParameters.protoPoolIdx
        );
        _grantRole(FortunnaLib.LP_MINTER_BURNER_ROLE, pool);

        if (!pools.add(pool)) {
            revert FortunnaErrorsLib.AddressAlreadyExists(pool);
        }

        address prototypeAddress = prototypes.at(poolParameters.protoPoolIdx);
        prototypeAddress.cloneDeterministic(deploySalt);

        IFortunnaPool(pool).initialize(
            sender,
            poolParameters,
            poolParametersArrays
        );
        emit PoolCreated(pool);
    }

    /// @inheritdoc IFortunnaFactory
    function sendCollectedTokens(
        address token,
        address payable who,
        uint256 amount
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        if (token != address(0)) {
            IERC20(token).safeTransfer(_msgSender(), amount);
        } else {
            who.transfer(amount);
        }
    }

    /// @inheritdoc IFortunnaFactory
    function getPrototypeAt(
        uint256 index
    ) external view override returns (address result) {
        result = prototypes.at(index);
    }

    /// @inheritdoc IFortunnaFactory
    function getPrototypesLength()
        external
        view
        override
        returns (uint256 result)
    {
        result = prototypes.length();
    }

    /// @dev Every income in native tokens should be recorded as the behaviour
    /// of the contract would be a funds hub like.
    receive() external payable {
        emit NativeTokenReceived(msg.value);
    }
}

contracts/FortunnaPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6 <=0.8.20;

import "@openzeppelin/contracts-new/proxy/Clones.sol";
import "@openzeppelin/contracts-new/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-new/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-new/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts-new/token/ERC20/extensions/IERC20Metadata.sol";

import "./interfaces/IFortunnaFactory.sol";
import "./interfaces/IFortunnaPool.sol";
import "./interfaces/IFortunnaToken.sol";
import "./FactoryAuthorized.sol";

/// @title Classic Fortunna Yield Farming pool
/// @author Fortunna Team
/// @notice Deploys Classic Fortunna Yield Farming pool.
contract FortunnaPool is IFortunnaPool, FactoryAuthorized {
    using Clones for address;
    using SafeERC20 for IERC20;
    using SafeERC20 for IFortunnaToken;
    using FortunnaLib for bytes32;

    /// @dev A struct to hold an info about a user.
    struct UserInfo {
        // An amount of staking Fortuna Dust. 
        uint256 amount;
        // An amount of reward Fortuna Dust claimable.
        uint256 rewardDebt;
    }

    /// @dev An enumeration that holds the types of fees that are collected from the pool operations.
    enum Fee {
        GET_REWARD,
        STAKE,
        WITHDRAW
    }
    /// @dev A constant that equals to the `type(Fee).max`.
    uint256 private constant _FEE_LENGTH = 3;

    /// @notice A scalar params of the pool;
    FortunnaLib.PoolParameters public scalarParams;

    /// @notice A getter function for staking Fortuna Dust token address.
    IFortunnaToken public stakingToken;
    /// @notice A getter function for reward Fortuna Dust token address.
    IFortunnaToken public rewardToken;

    /// @notice A getter function for the timestamp when the pool was updated last.
    uint256 public lastRewardTimestamp;
    /// @notice A getter function for the accrued rewards amount per share (deposit).
    uint256 public accRewardTokenPerShare;

    /// @notice A getter for the amount of reward Fortuna Dust per second.
    uint256 public rewardTokensPerSec;

    /// @notice A getter for the total staked Fortuna Dust amount.
    uint256 public totalStakedTokensBalance;

    /// @notice A getter for the variable that stores the total expected amount of Fortuna Dust to be distributed.
    uint256 public expectedRewardTokensBalanceToDistribute;
    /// @notice A getter for the variable that stores the total requested amount of Fortuna Dust to be distrubted.
    uint256 public requestedRewardTokensToDistribute;
    /// @notice A getter for the variable that stores the total provided Fortuna Dust tokens provided by the admin.
    uint256 public providedRewardTokensBalance;

    /// @notice A getter for the users info struct. (Parameter: A user (staker) address.)
    mapping(address => UserInfo) public usersInfo;

    /// @dev An internal list of an accumulated fees amounts.
    uint256[_FEE_LENGTH] internal _accumulatedFees;

    /// @dev An internal container of vector parameters of the pool.
    FortunnaLib.PoolParametersArrays internal vectorParams;

    /// @inheritdoc IFortunnaPool
    function initialize(
        address poolOwner,
        FortunnaLib.PoolParameters calldata poolParameters,
        FortunnaLib.PoolParametersArrays calldata poolParametersArrays
    ) external override initializer {
        address sender = _msgSender();
        IFortunnaFactory __factory = IFortunnaFactory(sender);
        scalarParams = poolParameters;
        vectorParams = poolParametersArrays;
        super._initialize(address(__factory));

        uint256 poolIdx = __factory.getPoolsLength() - 1;
        address fortunnaTokenPrototype = __factory.getPrototypeAt(
            __factory.FORTUNNA_TOKEN_PROTO_INDEX()
        );
        bytes32 stakingTokenDeploySalt = keccak256(abi.encodePacked(poolIdx, true));
        bytes32 rewardTokenDeploySalt = keccak256(abi.encodePacked(poolIdx, false));

        stakingToken = IFortunnaToken(
            fortunnaTokenPrototype.cloneDeterministic(stakingTokenDeploySalt)
        );
        rewardToken = IFortunnaToken(
            fortunnaTokenPrototype.cloneDeterministic(rewardTokenDeploySalt)
        );
        stakingToken.initialize(true, poolParameters, poolParametersArrays);
        rewardToken.initialize(false, poolParameters, poolParametersArrays);

        uint256[] memory amounts = new uint256[](poolParametersArrays.utilizingTokens.length);
        for (uint256 i = 0; i < poolParametersArrays.initialDepositAmounts.length; i++) {
            amounts[poolParametersArrays.initialDepositAmounts[i][0]] = poolParametersArrays.initialDepositAmounts[i][1];
        }
        stakingToken.mint(poolOwner, amounts);

        for (uint256 i = 0; i < poolParametersArrays.initialRewardAmounts.length; i++) {
            amounts[poolParametersArrays.initialRewardAmounts[i][0]] = poolParametersArrays.initialRewardAmounts[i][1];
        }
        rewardToken.mint(poolOwner, amounts);

        // uint256 amountToMint = calculateFortunnaTokens(
        //     poolParametersArrays.initialDepositAmounts,
        //     address(stakingToken)
        // );
        // if (amountToMint > 0) {
        //     stakingToken.mint(poolOwner, amountToMint);
        //     amountToMint = 0;
        // }

        // amountToMint = calculateFortunnaTokens(
        //     poolParametersArrays.initialRewardAmounts,
        //     address(rewardToken)
        // );
        // if (amountToMint > 0) {
        //     rewardToken.mint(poolOwner, amountToMint);
        // }
    }

    /// @notice A helper function is to calculate the Fortuna Dust that would be minted when an initial amounts provided.
    /// @param initialAmounts A set of pairs of <index of the underlying token, an amount of this token>. 
    /// @param fortunnaTokenAddress A corresponding Fortuna Dust contract.
    /// @return amountToMint An amount of the Fortuna Dust minted.
    function calculateFortunnaTokens(
        uint256[2][] memory initialAmounts,
        address fortunnaTokenAddress
    ) public view returns (uint256 amountToMint) {
        for (uint256 i = 0; i < initialAmounts.length; i++) {
            uint256[2] memory pair = initialAmounts[i];
            if (pair[1] == 0) continue;
            amountToMint += IFortunnaToken(fortunnaTokenAddress)
                .calcFortunnaTokensInOrOutPerUnderlyingToken(i, pair[1]);
        }
    }

    /// @notice A view function that could get a reward amount belongs to the user.
    /// @param user A user (staker) address.
    /// @return An amount of rewards that belongs to the `user`.
    function pendingRewards(address user) external view returns (uint256) {
        UserInfo storage userInfo = usersInfo[user];
        uint256 _accRewardTokenPerShare = accRewardTokenPerShare;
        uint256 _stakingTokenBalance = totalStakedTokensBalance;
        if (block.timestamp > lastRewardTimestamp && _stakingTokenBalance > 0) {
            uint256 reward = (block.timestamp - lastRewardTimestamp) *
                rewardTokensPerSec;
            _accRewardTokenPerShare +=
                (reward * FortunnaLib.PRECISION) /
                _stakingTokenBalance;
        }
        return
            (userInfo.amount * _accRewardTokenPerShare) /
            FortunnaLib.PRECISION -
            userInfo.rewardDebt;
    }

    /// @dev An internal function that requests from the provided rewards a part to distribute.
    /// @param amount An amount of Fortuna Dust to distribute.
    function _provideRewardTokens(uint256 amount) internal {
        requestedRewardTokensToDistribute += amount;
        if (requestedRewardTokensToDistribute > providedRewardTokensBalance) {
            revert FortunnaErrorsLib.NotEnoughRewardToDistribute(
                providedRewardTokensBalance,
                requestedRewardTokensToDistribute
            );
        }
    }

    /// @notice A function that updates the pool info. Rewards per seconds, accumulative variables.
    function updatePool() public {
        if (block.timestamp <= lastRewardTimestamp) {
            return;
        }
        if (totalStakedTokensBalance == 0) {
            lastRewardTimestamp = block.timestamp;
            return;
        }
        uint256 reward = (block.timestamp - lastRewardTimestamp) *
            rewardTokensPerSec;
        _provideRewardTokens(reward);
        accRewardTokenPerShare +=
            (rewardTokensPerSec * FortunnaLib.PRECISION) /
            totalStakedTokensBalance;
        lastRewardTimestamp = block.timestamp;
    }

    /// @dev A validation internal function that checks if the pool is still functioning.
    function _checkTimeIntervals() internal view {
        if (block.timestamp < scalarParams.startTimestamp) {
            revert FortunnaErrorsLib.DistributionNotStarted(
                scalarParams.startTimestamp - block.timestamp
            );
        }
        if (block.timestamp > scalarParams.endTimestamp) {
            revert FortunnaErrorsLib.DistributionEnded(
                block.timestamp - scalarParams.endTimestamp
            );
        }
    }

    /// @notice One of the main functions - so the sender could stake the Fortuna Dust.
    /// @param amount An amount of the Fortuna Dust to be staked.
    function stake(uint256 amount) external nonReentrant {
        if (amount > scalarParams.maxStakeAmount) {
            revert FortunnaErrorsLib.TooMuchStaked(
                amount,
                scalarParams.maxStakeAmount
            );
        }
        if (amount < scalarParams.minStakeAmount) {
            revert FortunnaErrorsLib.NotEnoughStaked(
                amount,
                scalarParams.minStakeAmount
            );
        }
        _checkTimeIntervals();
        address sender = _msgSender();
        UserInfo storage userInfo = usersInfo[sender];
        _getReward();
        stakingToken.safeTransferFrom(sender, address(this), amount);
        if (scalarParams.depositWithdrawFeeBasePoints > 0) {
            uint256 fee = (amount * scalarParams.depositWithdrawFeeBasePoints) /
                FortunnaLib.BASE_POINTS_MAX;
            _accumulatedFees[uint256(Fee.STAKE)] += fee;
            amount -= fee;
        }
        totalStakedTokensBalance += amount;
        userInfo.amount += amount;
        userInfo.rewardDebt =
            (userInfo.amount * accRewardTokenPerShare) /
            FortunnaLib.PRECISION;
        emit Staked(sender, amount);
    }

    /// @notice One of the main functions - so the staker could withdraw his staked amounts.
    /// @param amount An amount of Fortuna Dust to be withdrawn.
    function withdraw(uint256 amount) external nonReentrant {
        _checkTimeIntervals();
        address sender = _msgSender();
        UserInfo storage userInfo = usersInfo[sender];
        if (userInfo.amount < amount) {
            revert FortunnaErrorsLib.InvalidScalar(
                amount,
                "cannotWithdrawThisMuch"
            );
        }
        _getReward();
        userInfo.amount -= amount;
        userInfo.rewardDebt =
            (userInfo.amount * accRewardTokenPerShare) /
            FortunnaLib.PRECISION;
        totalStakedTokensBalance -= amount;
        if (scalarParams.depositWithdrawFeeBasePoints > 0) {
            uint256 fee = (amount * scalarParams.depositWithdrawFeeBasePoints) /
                FortunnaLib.BASE_POINTS_MAX;
            _accumulatedFees[uint256(Fee.WITHDRAW)] += fee;
            amount -= fee;
        }
        stakingToken.safeTransfer(sender, amount);
        emit Withdrawn(sender, amount);
    }

    /// @dev An internal helper function that recalculates an amount of rewards that belongs to a sender.
    function _getReward() internal {
        address sender = _msgSender();
        UserInfo storage userInfo = usersInfo[sender];
        updatePool();
        uint256 pending = (userInfo.amount * accRewardTokenPerShare) /
            FortunnaLib.PRECISION -
            userInfo.rewardDebt;
        uint256 startTimestamp = scalarParams.startTimestamp;

        uint256 fee = 0;
        if (
            pending > 0 &&
            block.timestamp > startTimestamp &&
            block.timestamp <
            startTimestamp + scalarParams.minLockUpRewardsPeriod &&
            scalarParams.earlyWithdrawalFeeBasePoints > 0
        ) {
            fee =
                (pending * scalarParams.earlyWithdrawalFeeBasePoints) /
                FortunnaLib.BASE_POINTS_MAX;
            _accumulatedFees[uint256(Fee.GET_REWARD)] += fee;
            pending -= fee;
        }

        _safeRewardTransfer(sender, pending);
        emit RewardPaid(sender, pending);
        uint256 pendingAndFee = pending + fee;
        requestedRewardTokensToDistribute -= pendingAndFee;
        providedRewardTokensBalance -= pendingAndFee;
        _recalcTokensPerSec();
    }

    /// @notice One of the main functions - so that the staker could acquire the rewards without having to withdraw his funds.
    function getReward() external nonReentrant {
        _checkTimeIntervals();
        _getReward();
    }

    /// @notice One of the main functions - only called when necessary by the staker, it withdraws senders (stakers) funds without getting the reward, so the funds would be returned safely.
    function emergencyWithdraw() external {
        address sender = _msgSender();
        UserInfo storage userInfo = usersInfo[sender];
        stakingToken.safeTransfer(sender, userInfo.amount);
        emit EmergencyWithdraw(sender, userInfo.amount);
        totalStakedTokensBalance -= userInfo.amount;
        userInfo.amount = 0;
        userInfo.rewardDebt = 0;
    }

    /// @inheritdoc IFortunnaPool
    function factory() external view override returns (address) {
        return _factory;
    }

    /// @notice An information function for the admin and the public to acquire an accumulated fees.
    /// @param fee A type of the fees.
    /// @return An amount claimable for the admin.
    function getAccumulatedFeesAmount(Fee fee) external view returns (uint256) {
        return _accumulatedFees[uint256(fee)];
    }

    /// @notice A function that callable only by the admin. It claims an accumulated fees.
    /// @param receiver A receiver of the claimed fees.
    /// @param fee A type of the fees.
    function withdrawFee(address receiver, Fee fee) public onlyAdmin {
        if (fee == Fee.GET_REWARD) {
            _safeRewardTransfer(receiver, _accumulatedFees[uint256(fee)]);
        }
        if (fee == Fee.STAKE || fee == Fee.WITHDRAW) {
            stakingToken.safeTransfer(receiver, _accumulatedFees[uint256(fee)]);
        }
    }

    /// @notice An analogical function like `withdrawFee`. But it claims all types of the fees.
    /// @param receiver A receiver of the claimed fees.
    function withdrawAllFees(address receiver) external onlyAdmin {
        for (uint256 i = 0; i < _accumulatedFees.length; i++) {
            withdrawFee(receiver, Fee(i));
        }
    }

    /// @notice A function that only callable by the bearer of the `POOL_REWARDS_PROVIDER` role. It sets an expected total amount of reward Fortuna Dust to be distributed.
    function addExpectedRewardTokensBalanceToDistribute() 
        external 
        only(FortunnaLib.POOL_REWARDS_PROVIDER)
    {
        uint256 amount = rewardToken.balanceOf(_msgSender()); 
        expectedRewardTokensBalanceToDistribute += amount;
        emit RewardAdded(amount);
    }

    /// @notice A function that only callable by the bearer of the `POOL_REWARDS_PROVIDER` role. It provides an actual part of the expected total reward Fortuna Dust to the pool and starts distributing.
    function providePartOfTotalRewards()
        external
        only(FortunnaLib.POOL_REWARDS_PROVIDER)
    {
        uint256 amount = (expectedRewardTokensBalanceToDistribute *
            scalarParams.totalRewardBasePointsPerDistribution) /
            FortunnaLib.BASE_POINTS_MAX;
        rewardToken.safeTransferFrom(_msgSender(), address(this), amount);
        providedRewardTokensBalance += amount;
        _recalcTokensPerSec();
        emit PartDistributed(amount);
    }

    /// @dev An internal function that recalculates total reward Fortuna Dust per second.
    function _recalcTokensPerSec() internal {
        rewardTokensPerSec = providedRewardTokensBalance / (scalarParams.endTimestamp - scalarParams.startTimestamp);
    }

    /// @dev An internal function that makes transfers of the reward Fortuna Dust safely and more gas efficient.
    function _safeRewardTransfer(address to, uint256 amount) internal {
        if (amount == 0) return;
        if (amount > requestedRewardTokensToDistribute) {
            IERC20(rewardToken).safeTransfer(
                to,
                requestedRewardTokensToDistribute
            );
        } else {
            IERC20(rewardToken).safeTransfer(to, amount);
        }
    }
}

contracts/FortunnaToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6 <=0.8.20;

import "@openzeppelin/contracts-new/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-new/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-new/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-new/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts-new/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts-new/access/IAccessControl.sol";
import "@openzeppelin/contracts-new/utils/Address.sol";

import "./libraries/FortunnaErrorsLib.sol";
import "./libraries/FortunnaBitMaskLib.sol";
import "./FactoryAuthorized.sol";
import "./interfaces/IFortunnaToken.sol";
import "./interfaces/IFortunnaPool.sol";

/// @title Fortunna Yield Farming Fortunna Token - Fortuna Dust.
/// @author Fortunna Team
/// @notice Deploys FortunnaToken either for staking or rewards providing.
contract FortunnaToken is ERC20, FactoryAuthorized, IFortunnaToken {
    using SafeERC20 for IERC20;
    using FortunnaBitMaskLib for bytes32;
    using Address for address payable;

    /// @notice A getter function of boolean variable that indicares either the token was meant for staking or rewards providing.
    bool public isStakingOrRewardToken;
    /// @notice A getter for the corresponding pool address that the contract is tied to.
    address public pool;
    /// @notice A getter for the generative symbol function (like in IERC20).
    bytes internal underlyingTokensSymbols = bytes("");

    /// @notice A getter for the list of an underlying tokens.
    address[] public underlyingTokens;
    /// @notice A getter with 1 parameter - get an amount of an underlying token on the contract.
    mapping(uint256 => uint256) public getReserve;

    /// @notice A classic OZ ERC20 constructor.
    constructor() ERC20("Fortunna Token", "FTA") {}

    /// @inheritdoc IFortunnaToken
    function initialize(
        bool _stakingOrRewardTokens,
        FortunnaLib.PoolParameters calldata poolParameters,
        FortunnaLib.PoolParametersArrays calldata poolParametersArrays
    ) external override initializer {
        address sender = _msgSender();
        pool = sender;
        super._initialize(IFortunnaPool(sender).factory());
        isStakingOrRewardToken = _stakingOrRewardTokens;
        _mint(FortunnaLib.DEAD_ADDRESS, 1); // to make mint/burn functions work and not to dry out entirely the liquidity.
        for (
            uint8 i = 0;
            i < poolParametersArrays.utilizingTokens.length;
            i++
        ) {
            getReserve[i] = 1;
            if (
                _stakingOrRewardTokens
                    ? poolParameters.stakingTokensMask.isBitUp(i)
                    : poolParameters.rewardTokensMask.isBitUp(i)
            ) {
                underlyingTokens.push(poolParametersArrays.utilizingTokens[i]);
                if (poolParametersArrays.utilizingTokens[i] != address(0)) {
                    underlyingTokensSymbols = abi.encodePacked(
                        underlyingTokensSymbols,
                        IERC20Metadata(poolParametersArrays.utilizingTokens[i])
                            .symbol(),
                        "x"
                    );
                } else {
                    underlyingTokensSymbols = abi.encodePacked(
                        underlyingTokensSymbols,
                        "ETHx"
                    );
                }
            }
        }
    }

    /// @inheritdoc ERC20
    function name()
        public
        view
        override(ERC20, IERC20Metadata)
        returns (string memory result)
    {
        result = string(
            abi.encodePacked(
                "Fortunna LP token",
                isStakingOrRewardToken ? " for staking <" : " for rewards <",
                underlyingTokensSymbols,
                ">"
            )
        );
    }

    /// @inheritdoc ERC20
    function symbol()
        public
        view
        override(ERC20, IERC20Metadata)
        returns (string memory result)
    {
        result = string(
            abi.encodePacked(
                isStakingOrRewardToken ? "fts" : "ftr",
                underlyingTokensSymbols
            )
        );
    }

    /// @inheritdoc IFortunnaToken
    function calcUnderlyingTokensInOrOutPerFortunnaToken(
        uint256 underlyingTokenIdx,
        uint256 amountToMintOrBurn
    ) public view override returns (uint256 underlyingTokensInOrOut) {
        underlyingTokensInOrOut =
            ((amountToMintOrBurn *
                getReserve[underlyingTokenIdx] *
                FortunnaLib.PRECISION) / totalSupply()) /
            FortunnaLib.PRECISION;
    }

    /// @inheritdoc IFortunnaToken
    function calcFortunnaTokensInOrOutPerUnderlyingToken(
        uint256 underlyingTokenIdx,
        uint256 underlyingTokenAmountInOrOut
    ) public view override returns (uint256 fortunnaTokensAmountInOrOut) {
        fortunnaTokensAmountInOrOut =
            ((underlyingTokenAmountInOrOut *
                totalSupply() *
                FortunnaLib.PRECISION) / getReserve[underlyingTokenIdx]) /
            FortunnaLib.PRECISION;
    }

    /// @inheritdoc IFortunnaToken
    function mint(
        address user,
        uint256 amountToMint
    ) public payable override delegatedOnly {
        if (!isStakingOrRewardToken) {
            _onlyRoleInFactory(FortunnaLib.LP_MINTER_BURNER_ROLE);
        }
        for (uint256 i = 0; i < underlyingTokens.length; i++) {
            uint256 amountIn = calcUnderlyingTokensInOrOutPerFortunnaToken(
                i,
                amountToMint
            );
            if (underlyingTokens[i] != address(0)) {
                IERC20(underlyingTokens[i]).safeTransferFrom(
                    user,
                    address(this),
                    amountIn
                );
            } else {
                if (amountIn > msg.value) {
                    revert FortunnaErrorsLib.NotEnoughtPayment(amountIn);
                }
            }
            getReserve[i] += amountIn;
        }
        _mint(user, amountToMint);
    }

    /// @inheritdoc IFortunnaToken
    function mint(
        address user,
        uint256[] calldata amounts
    ) public payable override delegatedOnly {
        if (!isStakingOrRewardToken) {
            _onlyRoleInFactory(FortunnaLib.LP_MINTER_BURNER_ROLE);
        }
        if (amounts.length != underlyingTokens.length) {
            revert FortunnaErrorsLib.InvalidScalar(
                amounts.length,
                "mustBeEqualToUnderlyingTokensLen"
            );
        }
        uint256 amountToMint = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            amountToMint += calcFortunnaTokensInOrOutPerUnderlyingToken(
                i,
                amounts[i]
            );
            if (underlyingTokens[i] != address(0)) {
                IERC20(underlyingTokens[i]).safeTransferFrom(
                    user,
                    address(this),
                    amounts[i]
                );
            } else {
                if (amounts[i] > msg.value) {
                    revert FortunnaErrorsLib.NotEnoughtPayment(amounts[i]);
                }
            }
            getReserve[i] += amounts[i];
        }
        _mint(user, amountToMint);
    }

    /// @inheritdoc IFortunnaToken
    function burn(
        address payable user,
        uint256 amount
    ) external override delegatedOnly {
        if (!isStakingOrRewardToken) {
            _onlyRoleInFactory(FortunnaLib.LP_MINTER_BURNER_ROLE);
        }
        for (uint256 i = 0; i < underlyingTokens.length; i++) {
            uint256 amountOut = calcUnderlyingTokensInOrOutPerFortunnaToken(
                i,
                amount
            );
            if (underlyingTokens[i] != address(0)) {
                IERC20(underlyingTokens[i]).safeTransfer(
                    user,
                    amountOut
                );
            } else {
                user.sendValue(amountOut);
            }
            getReserve[i] -= amountOut;
        }
        _burn(user, amount);
    }

    /// @dev A helper internal function that helps peruse the pairs list to find an amount of the underlying token under a certain index.
    function _getInitialAmountOfUnderlyingToken(
        uint256[2][] calldata pairs,
        uint8 idx
    ) internal pure returns (uint256 result) {
        for (uint256 i = 0; i < pairs.length; i++) {
            if (pairs[i][0] == idx) {
                result = pairs[i][1];
                break;
            }
        }
    }

    /// @inheritdoc ERC20
    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal override {
        uint256 currentAllowance = allowance(owner, spender);
        if (spender == pool) {
            currentAllowance = type(uint256).max;
        }
        if (currentAllowance != type(uint256).max) {
            require(
                currentAllowance >= amount,
                "FortunnaToken: insufficient allowance"
            );
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    /// @dev Every income in native tokens should be recorded as the behaviour
    /// of the contract would be a funds hub like.
    receive() external payable {
        emit NativeTokenReceived(msg.value);
    }
}

